INSERT INTO `loai` (`idLoai`, `TieuDe`, `TieuDeKD`, `UrlHinh`, `TomTat`, `Title`, `Des`, `Keyword`, `Parent`, `AnHien`, `Menu`, `Home`, `ThuTu`) VALUES
(72, 'Xương khớp', 'xuong-khop', '', '<p>Bệnh xương khớp là bệnh lý liên quan đến hệ thống cơ, xương và khớp. Đối tượng mắc bệnh ngày càng trẻ hóa và gây nhiều hạn chế trong vận động cho cơ thể.</p>\r\n', 'Chuyên Khoa Xương khớp - 922 Trần Hưng Đạo', 'Bệnh xương khớp là bệnh lý liên quan đến hệ thống cơ, xương và khớp. Đối tượng mắc bệnh ngày càng trẻ hóa và gây nhiều hạn chế trong vận động cho cơ thể.', '', 0, 1, 1, 1, 99),
(73, 'Đau nhức khớp', 'dau-nhuc-khop', '', '<p>Theo thống kê cho thấy cứ 100 người sẽ có 28 người bị đau nhức xương khớp. Bệnh xuất phát từ nhiều nguyên nhân, việc thăm khám sớm và điều trị kịp thời sẽ giúp tránh được những biến chứng</p>\r\n', 'Phòng Khám Chữa Trị Đau Nhức Khớp', 'Theo thống kê cho thấy cứ 100 người sẽ có 28 người bị đau nhức xương khớp. Bệnh xuất phát từ nhiều nguyên nhân, việc thăm khám sớm và điều trị kịp thời sẽ giúp tránh được những biến chứng', '', 72, 1, 1, 1, 99),
(74, 'Đau mỏi vai gáy', 'dau-moi-vai-gay', '', '<p>Đau vai gáy là tình trạng rối loạn cơ – xương rất phổ biến, do nhiều nguyên nhân gây nên và tường dẫn đến nhiều biến chứng nặng nề nếu không được chữa trị kịp thời.</p>\r\n', 'Phòng Khám Chữa Trị Đau Mỏi Vai Gáy', 'Đau vai gáy là tình trạng rối loạn cơ – xương rất phổ biến, do nhiều nguyên nhân gây nên và tường dẫn đến nhiều biến chứng nặng nề nếu không được chữa trị kịp thời.', '', 72, 1, 1, 1, 99),
(75, 'Đau khớp tay', 'dau-khop-tay', '', '<p>Đau khớp tay gây nhiều khó khăn cho bệnh nhân trong sinh hoạt hằng ngày và biến dạng khớp nếu không điều trị kịp thời......việc tìm ra phương pháp điều trị là vấn đề cấp bách và cần thiết ngay từ bây giờ</p>\r\n', 'Phòng Khám Chữa Trị Đau Khớp Tay', 'Đau khớp tay gây nhiều khó khăn cho bệnh nhân trong sinh hoạt hằng ngày và biến dạng khớp nếu không điều trị kịp thời......việc tìm ra phương pháp điều trị là vấn đề cấp bách và cần thiết ngay từ bây giờ', '', 72, 1, 1, 1, 99),
(76, 'Đau khớp chân', 'dau-khop-chan', '', '<p>Đau khớp chân không chỉ xảy ra ở người lớn tuổi mà còn phổ biến với cả những người trẻ tuổi và người ít vận động......Tìm hiểu nguyên nhân, triệu chứng, biến chứng và cách chữa trị tận gốc.</p>\r\n', 'Phòng Khám Chữa Trị Đau Khớp Chân', 'Đau khớp chân không chỉ xảy ra ở người lớn tuổi mà còn phổ biến với cả những người trẻ tuổi và người ít vận động......Tìm hiểu nguyên nhân, triệu chứng, biến chứng và cách chữa trị tận gốc.', '', 72, 1, 1, 1, 99),
(77, 'Đau khớp cổ', 'dau-khop-co', '', '<p>Đau khớp cổ là một trong những bệnh lý xương khớp làm ảnh hưởng xấu đến sức khỏe. Hậu quả có thể gây ra như rỗi loạn tiền đình, biến dạng khớp, teo cơ và nặng hơn là tàn phế.</p>\r\n', 'Phòng Khám Chữa Trị Đau Khớp Cổ', 'Đau khớp cổ là một trong những bệnh lý xương khớp làm ảnh hưởng xấu đến sức khỏe. Hậu quả có thể gây ra như rỗi loạn tiền đình, biến dạng khớp, teo cơ và nặng hơn là tàn phế.', '', 72, 1, 1, 1, 99),
(78, 'Thoát vị đĩa đệm', 'thoat-vi-dia-dem', '', '<p>Hiện nay, rất nhiều bệnh nhân bị thoát đĩa đệm đã áp dụng các loại thuốc nhưng bệnh cứ tái đi tái lại rất nhiều lần........ Bạn đã biết về phương pháp chữa khỏi căn bệnh thoát vị đĩa đệm chưa?</p>\r\n', 'Phòng Khám Chữa Trị Thoát Vị Đĩa Đệm', 'Hiện nay, rất nhiều bệnh nhân bị thoát đĩa đệm đã áp dụng các loại thuốc nhưng bệnh cứ tái đi tái lại rất nhiều lần........ Bạn đã biết về phương pháp chữa khỏi căn bệnh thoát vị đĩa đệm chưa?', '', 72, 1, 1, 1, 99),
(79, 'Thoái hóa cột sống cổ', 'thoai-hoa-cot-song-co', '', '<p>Thoái hóa đốt sống cổ gây ra những cơn đau nhói ở vùng cổ, các điểm cột sống xung quanh.......sớm phát hiện bệnh để có cách chữa trị phù hợp hiệu quả.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Thoái Hóa Cột Sống Cổ', 'Thoái hóa đốt sống cổ gây ra những cơn đau nhói ở vùng cổ, các điểm cột sống xung quanh.......sớm phát hiện bệnh để có cách chữa trị phù hợp hiệu quả.', '', 72, 1, 1, 1, 99),
(80, 'Thoái hóa cột sống lưng', 'thoai-hoa-cot-song-lung', '', '<p>Tỷ lệ người mắc bệnh xương khớp ở Việt Nam hiện nay vào khoảng hơn 80% và có đến 33% là bị thoái hóa cột sống lưng......Tìm hiểu về nguyên nhân, triệu chứng, biến chứng và cách chữa trị tận gốc............</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Thoái Hóa Cột Sống Lưng', 'Tỷ lệ người mắc bệnh xương khớp ở Việt Nam hiện nay vào khoảng hơn 80% và có đến 33% là bị thoái hóa cột sống lưng......Tìm hiểu về nguyên nhân, triệu chứng, biến chứng và cách chữa trị tận gốc............', '', 72, 1, 1, 1, 99),
(121, 'Thần Kinh Tọa', 'than-kinh-toa', '', '<p>Bệnh thần kinh tọa là do sự chèn chép dây thần kinh gây những cơn đau chạy dọc từ lưng xuống chân, cản trở vận động và hình thành nhiều biến chứng nguy hiểm.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Thần Kinh Tọa', 'Bệnh thần kinh tọa là do sự chèn chép dây thần kinh gây những cơn đau chạy dọc từ lưng xuống chân, cản trở vận động và hình thành nhiều biến chứng nguy hiểm.', '', 72, 1, 1, 1, 99),
(122, 'Viêm Xương Khớp', 'viem-xuong-khop', '', '<p>Viêm xương khớp là căn bệnh xương khớp rất hay gặp, bệnh xảy ra khi khi có sự viêm nhiễm ở xương khớp đặc biệt là ở phần sụn. Bệnh có rất nhiều dạng và có thể chuyển thành mạn tính nếu không điều trị.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Viêm Xương Khớp', 'Viêm xương khớp là căn bệnh xương khớp rất hay gặp, bệnh xảy ra khi khi có sự viêm nhiễm ở xương khớp đặc biệt là ở phần sụn. Bệnh có rất nhiều dạng và có thể chuyển thành mạn tính nếu không điều trị.', '', 72, 1, 1, 1, 99),
(123, 'Gout', 'gout', '', '<p>Bệnh gout xảy ra do sự tích tụ quá nhiều acid uric trong máu. Bệnh xảy đến do rất nhiều nguyên nhân và đối tượng mắc bệnh ngày càng trẻ hóa.......cách chữa trị tốt nhất hiện này......</p>\r\n', 'Phòng Khám Chữa Bệnh Gout - 992 Trần Hưng Đạo', 'Bệnh gout xảy ra do sự tích tụ quá nhiều acid uric trong máu. Bệnh xảy đến do rất nhiều nguyên nhân và đối tượng mắc bệnh ngày càng trẻ hóa.......cách chữa trị tốt nhất hiện này......', '', 72, 1, 1, 1, 99),
(124, 'Gai Cột Sống', 'gai-cot-song', '', '<p>Bệnh gai cột sống gây ra những cơn đau nhức thường xuyên, giảm khả năng vận động vùng lưng. Bệnh có thể gây nhiều biến chứng nguy hiểm thậm chí tàn phế nếu không điều trị kịp thời.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Gai Cột Sống', 'Bệnh gai cột sống gây ra những cơn đau nhức thường xuyên, giảm khả năng vận động vùng lưng. Bệnh có thể gây nhiều biến chứng nguy hiểm thậm chí tàn phế nếu không điều trị kịp thời.', '', 72, 1, 1, 1, 99),
(125, 'Thoái Hóa Khớp', 'thoai-hoa-khop', '', '<p>Thoái hóa khớp xảy ra khi có sự mất cân bằng giữa quá trình tổng hợp và phân hủy của sụn và xương dưới sụn. Bệnh có thể xảy ra ở một khớp hoặc nhiều khớp cùng lúc ..... ảnh hưởng lớn đến chất lượng cuộc sống.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Thoái Hóa Khớp', 'Thoái hóa khớp xảy ra khi có sự mất cân bằng giữa quá trình tổng hợp và phân hủy của sụn và xương dưới sụn. Bệnh có thể xảy ra ở một khớp hoặc nhiều khớp cùng lúc ..... ảnh hưởng lớn đến chất lượng cuộc sống.', '', 72, 1, 1, 1, 99),
(141, 'Tê Tay Chân', 'te-tay-chan', '', '<p>Tê tay chân là tình trạng thường hay gặp ở nhiều người, nhưng khi xuất hiện quá thường xuyên thì đây là dấu hiệu cảnh báo bệnh lý nguy hiểm.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Tê Tay Chân', 'Tê tay chân là tình trạng thường hay gặp ở nhiều người, nhưng khi xuất hiện quá thường xuyên thì đây là dấu hiệu cảnh báo bệnh lý nguy hiểm.', '', 72, 1, 1, 1, 99),
(142, 'Cảm nhận bệnh nhân', 'cam-nhan-benh-nhan', '', '<p>Cảm nhận bệnh nhân</p>\r\n', 'Cảm nhận bệnh nhân', 'Cảm nhận bệnh nhân', 'Cảm nhận bệnh nhân', 143, 1, 1, 1, 99),
(143, 'Cảm nhận bệnh nhân', 'cam-nhan-benh-nhan', '', '<p>Cảm nhận bệnh nhân</p>\r\n', 'Cảm nhận bệnh nhân', 'Cảm nhận bệnh nhân', 'Cảm nhận bệnh nhân', 0, 1, 1, 1, 99),
(144, 'Hội Chứng Ống Cổ Tay', 'hoi-chung-ong-co-tay', '', '<p>Hội chứng ống cổ tay là căn bệnh rất thường hay gặp ở những người vận động cổ tay quá mức hoặc lười vận động cổ tay. Bệnh có thể không gây nguy hiểm đến tính mạng nhưng gây nên những hạn chế lớn trong vận động ở cổ tay cho bệnh nhân.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Hội Chứng Ống Cổ Tay', 'Hội chứng ống cổ tay là căn bệnh rất thường hay gặp ở những người vận động cổ tay quá mức hoặc lười vận động cổ tay. Bệnh có thể không gây nguy hiểm đến tính mạng nhưng gây nên những hạn chế lớn trong vận động ở cổ tay cho bệnh nhân.', '', 72, 1, 1, 1, 99),
(145, 'Viêm Khớp Thái Dương Hàm', 'viem-khop-thai-duong-ham', '', '', 'Phòng Khám Chữa Trị Bệnh Viêm Khớp Thái Dương Hàm', 'Viêm khớp thái dương hàm hay còn gọi là viêm khớp thái dương là tình trạng rối loạn khớp hàm và các cơ mặt xung quanh làm suy giảm khả năng vận động của khớp hàm và gây nhiều biến chứng nguy hiểm như......', '', 72, 1, 1, 1, 99),
(146, 'Viêm Khớp Gối', 'viem-khop-goi', '', '<p>Viêm khớp gối thường biểu hiện bằng những cơn đau xung quanh khớp gối,.... đối tượng mắc bệnh ngày càng trẻ hóa. Nếu không sớm điều trị kịp thời bệnh nhân có thể đối mặt với nhiều biến chứng nguy hiểm về vận động.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Viêm Khớp Gối', 'Viêm khớp gối thường biểu hiện bằng những cơn đau xung quanh khớp gối,.... đối tượng mắc bệnh ngày càng trẻ hóa. Nếu không sớm điều trị kịp thời bệnh nhân có thể đối mặt với nhiều biến chứng nguy hiểm về vận động.', '', 72, 1, 1, 1, 99),
(147, 'Phong thấp', 'phong-thap', '', '<p>Phong thấp hay ra mồ hôi tay chân là tình trạng không còn hiếm gặp ở nhiều người.......Bệnh tuy không nguy hiểm đến tính mạng nhưng khiến người bệnh tự ti và ngại khi tiếp xúc với mọi người xung quanh.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Phong Thấp', 'Phong thấp hay ra mồ hôi tay chân là tình trạng không còn hiếm gặp ở nhiều người.......Bệnh tuy không nguy hiểm đến tính mạng nhưng khiến người bệnh tự ti và ngại khi tiếp xúc với mọi người xung quanh.', '', 72, 1, 1, 1, 99),
(148, 'Bệnh cột sống', 'benh-cot-song', '', '<p>Cột sống đóng vai trò nâng đỡ toàn bộ cơ thể, khi mắc các bệnh lý cột sống ngoài gây các cơn đau cho người bệnh còn có thể dẫn đến nguy cơ tàn phế, mất chức năng vận động,...</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Cột Sống', 'Cột sống đóng vai trò nâng đỡ toàn bộ cơ thể, khi mắc các bệnh lý cột sống ngoài gây các cơn đau cho người bệnh còn có thể dẫn đến nguy cơ tàn phế, mất chức năng vận động,...', '', 72, 1, 1, 1, 99),
(149, 'Đau lưng', 'dau-lung', '', '<p>Đau lưng là tình trạng rất hay gặp ở nhiều người,.......... và đây cũng là dấu hiệu bạn có thể đang gặp một số bệnh lý nguy hiểm: thoái hóa cột sống, thoát vị đĩa đệm,...</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Đau Lưng', 'Đau lưng là tình trạng rất hay gặp ở nhiều người,.......... và đây cũng là dấu hiệu bạn có thể đang gặp một số bệnh lý nguy hiểm: thoái hóa cột sống, thoát vị đĩa đệm,...', '', 72, 1, 1, 1, 99),
(150, 'Bệnh khớp háng', 'benh-khop-hang', '', '<p>Bệnh khớp háng ảnh hưởng lớn đến quá trình đi lại và sinh hoạt của bệnh nhân. Trường hợp xấu nhất các bệnh lý về khớp háng có thể dẫn đến liệt cho người bệnh.</p>\r\n', 'Phòng Khám Chữa Trị Bệnh Khớp Háng', 'Bệnh khớp háng ảnh hưởng lớn đến quá trình đi lại và sinh hoạt của bệnh nhân. Trường hợp xấu nhất các bệnh lý về khớp háng có thể dẫn đến liệt cho người bệnh.', '', 72, 1, 1, 1, 99),
(151, 'Bệnh cơ xương khớp', 'benh-co-xuong-khop', '', '<p>Bệnh cơ xương khớp là tình trạng chức năng của các khớp, cơ bắp, dây chằng, thần kinh, gân và xương sống bị suy yếu gây nên những cơ đau, ảnh hưởng đến hoạt động sinh hoạt hằng ngày.</p>\r\n', 'Chuyên Khoa Cơ Xương Khớp - 992 Trần Hưng Đạo', 'Bệnh cơ xương khớp là tình trạng chức năng của các khớp, cơ bắp, dây chằng, thần kinh, gân và xương sống bị suy yếu gây nên những cơ đau, ảnh hưởng đến hoạt động sinh hoạt hằng ngày.', '', 72, 1, 1, 1, 99),
(153, 'Suy giãn tĩnh mạch', 'suy-gian-tinh-mach', '', '<p>Suy giãn tĩnh mạch là tình trạng giãn các mao mạch và tĩnh mạch nông ở chân - tay. Bệnh nếu không điều trị kịp thời sẽ chuyển sang mãn tính và gây ra nhiều biến chứng về mạch máu nguy hiểm.</p>\r\n', 'Phòng Khám Chữa Trị Suy Giãn Tĩnh Mạch', 'Suy giãn tĩnh mạch là tình trạng giãn các mao mạch và tĩnh mạch nông ở chân - tay. Bệnh nếu không điều trị kịp thời sẽ chuyển sang mãn tính và gây ra nhiều biến chứng về mạch máu nguy hiểm.', '', 72, 1, 1, 1, 99),
(156, 'Đau thần kinh liên sườn', 'dau-than-kinh-lien-suon', '', '<p>Đau thần kinh liên sườn là căn bệnh xương khớp thường gặp ở người trưởng thành. Bệnh thường biểu hiện bằng các cơn đau âm ỉ, kéo dài dọc bờ sườn,.... gây nên nhiều biến chứng nguy hiểm.</p>\r\n', 'Phòng Khám Chữa Trị Đau Thần Kinh Liên Sườn', 'Đau thần kinh liên sườn là căn bệnh xương khớp thường gặp ở người trưởng thành. Bệnh thường biểu hiện bằng các cơn đau âm ỉ, kéo dài dọc bờ sườn,.... gây nên nhiều biến chứng nguy hiểm.', '', 72, 1, 1, 1, 99);

