INSERT INTO `pages` (`idPa`, `idGroup`, `TieuDe`, `TieuDeKD`, `Title`, `Des`, `Keyword`, `TomTat`, `NoiDung`, `UrlHinh`, `NgayDang`, `AnHien`, `linkchuyen`, `co`) VALUES
(5, 1, 'GIỚI THIỆU', 'gioi-thieu', 'GIỚI THIỆU', 'Phòng khám y học cổ truyền An Đông là một cơ sở y tế điều trị chuyên nghiệp, kết hợp các hoạt động khám, điều trị, điều trị, phòng bệnh, chăm sóc sức khỏe cho người bệnh bằng phương pháp y học cổ truyền, tư vấn miễn phí và nghiên cứu khoa học đã và đang là một trong những phòng khám uy tín tại Tp Hồ Chí Minh hiện nay. Ngoài tiếp nhận các bệnh nhân tại Tp Hcm, hiện mỗi ngày phòng khám còn đón tiếp hơn 1000.000 lượt bệnh nhân đến thăm khám mỗi ngày từ các tỉnh thành lân cận khác như Long An, Đồng Nai, Tiền Giang, Bình Dương,…', '', '<p>Phòng khám y học cổ truyền An Đông là một cơ sở y tế điều trị chuyên nghiệp, kết hợp các hoạt động khám, điều trị, điều trị, phòng bệnh, chăm sóc sức khỏe cho người bệnh bằng phương pháp y học cổ truyền, tư vấn miễn phí và nghiên cứu khoa học đã và đang là một trong những phòng khám uy tín tại Tp Hồ Chí Minh hiện nay. Ngoài tiếp nhận các bệnh nhân tại Tp Hcm, hiện mỗi ngày phòng khám còn đón tiếp hơn 1000.000 lượt bệnh nhân đến thăm khám mỗi ngày từ các tỉnh thành lân cận khác như Long An, Đồng Nai, Tiền Giang, Bình Dương,…</p>\r\n', '<p>Phòng khám y học cổ truyền An Đông là một cơ sở y tế điều trị chuyên nghiệp, kết hợp các hoạt động khám, điều trị, điều trị, phòng bệnh, chăm sóc sức khỏe cho người bệnh bằng phương pháp y học cổ truyền, tư vấn miễn phí và nghiên cứu khoa học đã và đang là một trong những phòng khám uy tín tại Tp Hồ Chí Minh hiện nay. Ngoài tiếp nhận các bệnh nhân tại Tp Hcm, hiện mỗi ngày phòng khám còn đón tiếp hơn 1000.000 lượt bệnh nhân đến thăm khám mỗi ngày từ các tỉnh thành lân cận khác như Long An, Đồng Nai, Tiền Giang, Bình Dương,…</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://trihetbenhxuongkhop.com/upload/images/phong-kham/kham-benh-nhan.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:black; font-family:helvetica,sans-serif; font-size:18.0pt\">PHÒNG KHÁM ĐIỀU TRỊ BỆNH BẰNG ĐÔNG Y UY TÍN TẠI TP.HCM</span></strong></p>\r\n\r\n<p style=\"text-align:justify\">Với đội ngũ lương y, thầy thuốc cùng các chuyên gia có y thuật cao thâm, tinh thông lý luận học thuật cùng kinh nghiệm điều trị bệnh hơn 30 năm, Phòng khám Y học cổ truyền An Đông đã điều trị khỏi cho hàng ngàn bệnh nhân kể cả các bệnh lý mãn tính như viêm xoang, viêm mũi dị ứng, viêm họng, thoái hóa cột sống, đau nhức xương khớp, bệnh gút, chàm, vẩy nến, đau dạ dày, viêm đại tràng, rối loạn tiền đình, vô sinh, yếu sinh lý,…</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/upload/mtpk-an-dong/y-hoc-co-truyen-5.jpg\" /> <img alt=\"\" src=\"https://trihetbenhxuongkhop.com/upload/images/phong-kham/luong-y-nhu-tu-mau.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p>Kế thừa tinh hoa cũng như các y thuật từ cha ông xưa truyền lại, Y học Cổ truyền An Đông luôn tâm niệm “lương y như từ mẫu” lấy con người là gốc, bệnh chỉ là ngọn. Do đó, mục tiêu điều trị bệnh của đông y không chỉ chú trọng ở bệnh mà còn quan tâm đến chính khí của cơ thể. Khi thăm khám và điều trị bệnh tại Phòng khám Y học Cổ Truyền An Đông thì tùy theo nguyên nhân gây bệnh, tình trạng bệnh kèm theo cơ địa người bệnh mà các thầy thuốc của chúng tôi sẽ đưa ra những lý luận, biện chứng từ đó cho ra liệu trình phù hợp.</p>\r\n\r\n<p>Đây có thể là sự kết hợp giữa các bài thuốc đông y bí truyền đã được nghiên cứu, kiểm nghiệm cùng với châm cứu trị liệu, xoa bóp, bấm huyệt hay những món ăn vị thuốc có tính năng điều trị bệnh cao. Qua đó, tiến hành điều trị dứt điểm bệnh chỉ qua 1-2 liệu trình điều trị.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/upload/mtpk-an-dong/y-hoc-co-truyen-8.jpg\" /> <img alt=\"\" src=\"https://trihetbenhxuongkhop.com/upload/images/phong-kham/sanh.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p>Phòng Khám Y học Cổ truyền An Đông không chỉ đồng hành cùng bệnh nhân mà còn đồng hành cùng cộng đồng xã hội, ban lãnh đạo phòng khám thường xuyên tổ chức các buổi từ thiện nhằm giúp đỡ các những mảnh đời không may gặp khó khăn, tại phòng khám các bệnh nhân bệnh nặng chạy điều trị nhiều nơi tốn kém nhiều chi phí cũng được ban lãnh đạo phòng khám hỗ trợ để điều trị lành bệnh cho bệnh nhân. </p>\r\n\r\n<p>Hiện nay, rất nhiều người bệnh lo lắng mua phải thuốc giả, thuốc kém chất lượng, hơn nữa khi sử dụng thuốc dài ngày sẽ rất dễ bị tác dụng phụ gây suy thận, hại gan. Hiểu được tâm tư đó, Phòng khám Y học cổ truyền An Đông tiến hành quản lý chặt chẽ quá trình nhập thuốc, chế biến và bảo quản thuốc. </p>\r\n\r\n<p>Ngoài ra, người bệnh có thể hoàn toàn yên tâm, vì những thảo dược điều trị bệnh đều có nguồn gốc hoàn toàn từ thiên nhiên nên tuyệt đối an toàn và không gây ra bất kì tác dụng nào ảnh hưởng đến sức khỏe.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://trihetbenhxuongkhop.com/upload/images/phong-kham/phong-kham-an-dong.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p>Ngoài việc điều trị trị bệnh, đội ngũ y bác sĩ Phòng khám Y học Cổ truyền An Đông còn tập trung nghiên cứu nâng cao hiệu quả các bài thuốc gia truyền, trau dồi y đức để xứng đáng với sự tin yêu, mến phục của người bệnh. </p>\r\n\r\n<p>Bên cạnh đó, các kĩ thuật viên, nhân viên bào chế thuốc, đều được khuyến khích học tập để nâng cao trình độ chuyên môn nhằm nâng cao nghiệp vụ, cũng như hoàn thành mục tiêu đưa Phòng khám Y học Cổ truyền An Đông thành cơ sở điều trị bệnh bằng đông y đứng đầu cả nước.</p>\r\n\r\n<p>Đến với Phòng khám Y học Cổ truyền An Đông bạn sẽ luôn cảm nhận được sự quan tâm, ân cần, nhiệt huyết và tận tình của đội ngũ nhân viên, tập thể y bác sỹ trong suốt quá trình điều trị bệnh qua đó giúp xoa dịu nỗi đau bệnh tật để có ý chí chiến đấu với bệnh tật, sớm khỏi bệnh và hòa nhập với cộng đồng.</p>\r\n\r\n<p>Nhằm đem đến cho người bệnh một chất lượng dịch vụ cũng như môi trường điều trị bệnh tốt , hiện nay Phòng khám Y học Cổ truyền An Đông đang thực hiện chương trình tư vấn và đặt lịch hẹn khám qua mạng miễn phí. Ngoài ra khi đăng kí khám bệnh bạn sẽ được giảm 20% chi phí điều trị. Hãy đến với Y học cổ truyền An Đông để sức khỏe của bạn được chăm sóc toàn diện.</p>\r\n\r\n<div style=\"padding: 8px; border: 2px dashed #018ee6; word-wrap: break-word;\">\r\n<h2><strong>LIÊN HỆ:</strong></h2>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"color:#FF0000\"><strong><span style=\"font-family:segoe ui symbol,sans-serif; font-size:10pt\">☑️</span><span style=\"font-family:helvetica,sans-serif; font-size:10pt\"> Phòng khám Y Học Cổ Truyền An Đông</span></strong></span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"color:#FF0000\"><strong><span style=\"font-family:segoe ui symbol,sans-serif; font-size:10pt\">☑️</span><span style=\"font-family:helvetica,sans-serif; font-size:10pt\"> 992 Trần Hưng Đạo, Phường 7, Quận 5, TP.HCM</span></strong></span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"color:#FF0000\"><strong><span style=\"font-family:segoe ui symbol,sans-serif; font-size:10pt\">☑️</span><span style=\"font-family:helvetica,sans-serif; font-size:10pt\"> 0981.674.448 - 02866.709.555</span></strong></span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"color:#FF0000\"><strong><span style=\"font-family:segoe ui symbol,sans-serif; font-size:10pt\">☑️</span><span style=\"font-family:helvetica,sans-serif; font-size:10pt\"> Thứ 2 - Thứ 7: Từ 8:00 - 20:00</span></strong></span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"color:#FF0000\"><strong><span style=\"font-family:segoe ui symbol,sans-serif; font-size:10pt\">☑️</span><span style=\"font-family:helvetica,sans-serif; font-size:10pt\"> Chủ Nhật và các ngày lễ: Từ 8:00 - 17:00</span></strong></span></h3>\r\n</div>\r\n\r\n<h2><strong>MỤC ĐÍCH CỦA CHÚNG TÔI LÀ: </strong></h2>\r\n\r\n<div class=\"notes_cn\">\r\n<p><strong>- Giúp bệnh nhân hạn chế áp lực về bệnh tật.</strong></p>\r\n\r\n<p><strong>- Giúp bệnh nhân hạn chế áp lực về kinh tế.</strong></p>\r\n</div>\r\n', 'https://trihetbenhxuongkhop.com/upload/images/phong-kham/mat-bang-truoc.jpg', '2019-09-19 16:01:12', 1, '', 0),
(6, 1, 'Liên Hệ Phòng Khám Y Học Cổ Truyền', 'lien-he-phong-kham-y-hoc-co-truyen', 'Liên Hệ Phòng Khám Y Học Cổ Truyền', 'Phòng Khám Y Học Cổ Truyền là nơi tin tưởng của hàng nghìn bệnh nhân trong và ngoài nước, nhận được rất nhiều phản hồi tốt của bệnh nhân sau điều trị.', '', '<p><strong>Phòng Khám Y Học Cổ Truyền</strong><span style=\"font-size:18px\"> là nơi tin tưởng của hàng nghìn bệnh nhân trong và ngoài nước, nhận được rất nhiều phản hồi tốt của bệnh nhân sau điều trị. Phòng khám 100%  đầu tư của VN. Được Sở Y Tế TpHCM cấp phép số: 002084/HCM-CCHN</span></p>\r\n', '<p><span style=\"font-size:18px\">☑️ Phòng khám Y Học Cổ Truyền An Đông</span></p>\r\n\r\n<p><span style=\"font-size:18px\">☑️ 992 Trần Hưng Đạo, Phường 7, Quận 5, TP.HCM</span></p>\r\n\r\n<p><span style=\"font-size:18px\">☑️ 0981.674.448 - 02866.709.555</span></p>\r\n\r\n<p><span style=\"font-size:18px\">☑️ Thứ 2 - Thứ 7: Từ 8:00 - 20:00</span></p>\r\n\r\n<p><span style=\"font-size:18px\">☑️ Chủ Nhật và các ngày lễ: Từ 8:00 - 17:00</span></p>\r\n\r\n<p><span style=\"font-size:18px\">☑️ Giấy phép kinh doanh: 0313544914</span></p>\r\n\r\n<p><span style=\"font-size:18px\">☑️ Giấy phép hoạt động: 002084/HCM-CCHN</span></p>\r\n\r\n<p><span style=\"font-size:18px\">☑️ Giấy phép hành nghề: 002084/HCM-CCHN</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><iframe src=\"https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d7839.582848873818!2d106.66986943862307!3d10.75055210000001!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0xb1b51663b351e5b1!2zUGjDsm5nIEtow6FtIFkgSOG7jWMgQ-G7lSBUcnV54buBbiBBbiDEkMO0bmc!5e0!3m2!1svi!2s!4v1568885180240!5m2!1svi!2s\" width=\"800\" height=\"600\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\"></iframe></span></p>\r\n\r\n<h2><span style=\"font-size:24px\"><strong>Lý do bạn chọn YHCT An Đông</strong></span></h2>\r\n\r\n<p><span style=\"font-size:18px\">1️⃣ Phòng khám 100%  đầu tư của VN. Được Sở Y Tế TpHCM cấp phép số: <strong>002084/HCM-CCHN</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">2️⃣ Đội ngũ bác sĩ làm việc thăm khám và điều trị</span><span style=\"font-size:18px\"> bệnh tại phòng khám giỏi chuyên môn, giàu kinh nghiệm</span><span style=\"font-size:18px\"> – được xếp hạng thứ 1 trong top 5 bác sĩ đông y giỏi nhất ở Thành Phố Hồ Chí Minh. </span></p>\r\n\r\n<p><span style=\"font-size:18px\">3️⃣ Thuốc 100% từ thiên nhiên dạng thang được sàng lọc kỹ càng, chỉ lấy những phần hảo hạng, thuốc được đăng ký kiểm định theo thông tư  44/2014//TT-BYT chứng nhận an toàn sức khỏe</span></p>\r\n\r\n<p><span style=\"font-size:18px\">4️⃣ Thông thường 1 thang thuốc chỉ có 12-15 vị, bệnh nhân phải uống từ 1-2 tháng mới thấy hiệu quả, tại </span><span style=\"font-size:18px\"><strong>Phòng Khám Y Học Cổ Truyền An Đông</strong></span><span style=\"font-size:18px\"> 1 thang thuốc có từ 28 - trên 30 vị giúp hiệu quả điều trị rút ngắn 50~70% so với những phòng khám khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">5️⃣ </span><span style=\"font-size:18px\"><strong>Phòng Khám Y Học Cổ Truyền An Đông</strong></span><span style=\"font-size:18px\"> là nơi tin tưởng của hàng nghìn bệnh nhân trong và ngoài nước, nhận được rất nhiều phản hồi tốt của bệnh nhân sau điều trị.</span></p>\r\n', 'https://xuongkhop.dongy495.com/upload/images/phong-kham/IMG_4357_result.JPG', '2019-09-19 16:27:22', 1, '', 0);

